const loginLink=document.querySelector(".more-company");const loginPopup=document.querySelector(".modal-company");const loginClose=loginPopup.querySelector(".modal-close");const loginForm=loginPopup.querySelector(".feedback-form");const loginLogin=loginPopup.querySelector(".login-icon-user");const loginPassword=loginPopup.querySelector(".login-icon-password");let isStorageSupport=true;let storage="";try{storage=localStorage.getItem("login");}catch(err){isStorageSupport=false;} loginLink.addEventListener("click",function(evt){evt.preventDefault();loginPopup.classList.add("modal-show");if(storage){loginLogin.value=storage;loginPassword.focus();}else{loginLogin.focus();}});loginClose.addEventListener("click",function(evt){evt.preventDefault();loginPopup.classList.remove("modal-show");loginPopup.classList.remove("modal-error");});loginForm.addEventListener("submit",function(evt){if(!loginLogin.value||!loginPassword.value){evt.preventDefault();loginPopup.classList.remove("modal-error");loginPopup.offsetWidth=loginPopup.offsetWidth;loginPopup.classList.add("modal-error");}else{if(isStorageSupport){localStorage.setItem("login",loginLogin.value);}}});window.addEventListener("keydown",function(evt){if(evt.keyCode===27){if(loginPopup.classList.contains("modal-show")){evt.preventDefault();loginPopup.classList.remove("modal-show");loginPopup.classList.remove("modal-error");}}});var mapLink=document.querySelector(".contacts-button-map");var mapPopup=document.querySelector(".modal-map");var mapClose=mapPopup.querySelector(".modal-close");mapLink.addEventListener("click",function(evt){evt.preventDefault();mapPopup.classList.add("modal-show");});mapClose.addEventListener("click",function(evt){evt.preventDefault();mapPopup.classList.remove("modal-show");});window.addEventListener("keydown",function(evt){if(evt.keyCode===27){if(mapPopup.classList.contains("modal-show")){mapPopup.classList.remove("modal-show");}}});